            // Prevent default posting of form - put here to work in case of errors
            event.preventDefault();

            // Fire off the request to
            request = jQuery.ajax({
                url: "/saat/global_index/orderTracking",
                type: "post",
                data: jQuery(this).serialize(),
                dataType: 'json'
            });

